    var date = "Jan";
}
else if(Today.getMonth()+1 == '2'){
	var date = "Feb";
}
else if(Today.getMonth()+1 == '3'){
	var date = "Mar";
  }  
else if(Today.getMonth()+1 =='4'){
	var date = "Apr";
}    
else if(Today.getMonth()+1 == '5'){
	var date = "May";
}    
else if(Today.getMonth()+1 == '6'){
	var date = "Jun";
 }   
else if(Today.getMonth()+1 == '7'){
	var date = "Jul";
}    
else if(Today.getMonth()+1 == '8'){
	var date = "Aug";
  }  
else if(Today.getMonth()+1 == '9'){
	var date = "Sep";
 }   
else if(Today.getMonth()+1 == '10'){
	var date = "Oct";
 }   
else if(Today.getMonth()+1 == '11'){
	var date = "Nov";
}

else{
	var date = "Dec";
}
//return date;
if(parseInt(Today.getDate()) % 10 == 1 && Today.getDate()!=11){
	date += " " + Today.getDate()+ "st,   " + Today.getFullYear();
}
else if(parseInt(Today.getDate()) % 10 == 2 && Today.getDate()!=12){
    date += " " + Today.getDate()+ "nd,   " + Today.getFullYear();
}
else if(parseInt(Today.getDate()) % 10 == 3 && Today.getDate()!=13){
    date += " " + Today.getDate()+ "rd,   " + Today.getFullYear();
}
else{
    date += " " + Today.getDate()+ "th,   " + Today.getFullYear();
}
return date;
